// FunÃ§Ãµes auxiliares
export const getFurnitureByCategory = (categoryId: string): FurnitureItem[] => {
  return furnitureLibrary.filter(item => item.category === categoryId);
};

export const getFurnitureById = (id: string): FurnitureItem | undefined => {
  return furnitureLibrary.find(item => item.id === id);
};

export const searchFurniture = (query: string): FurnitureItem[] => {
  const lowerQuery = query.toLowerCase();
  return furnitureLibrary.filter(item => 
    item.name.toLowerCase().includes(lowerQuery) ||
    item.tags?.some(tag => tag.toLowerCase().includes(lowerQuery)) ||
    item.subcategory.toLowerCase().includes(lowerQuery)
  );
};

export const getPopularFurniture = (): FurnitureItem[] => {
  return furnitureLibrary.filter(item => item.popular);
};

export const getPremiumFurniture = (): FurnitureItem[] => {
  return furnitureLibrary.filter(item => item.premium);
};

export const getFurnitureBySubcategory = (subcategoryId: string): FurnitureItem[] => {
  return furnitureLibrary.filter(item => item.subcategory === subcategoryId);
};

export const getFurnitureByMaterial = (material: string): FurnitureItem[] => {
  return furnitureLibrary.filter(item => item.materials?.includes(material));
};

export const getFurnitureCount = (): number => furnitureLibrary.length;

export const getSubcategories = (categoryId: string): string[] => {
  const items = getFurnitureByCategory(categoryId);
  return [...new Set(items.map(item => item.subcategory))];
};


